mapper.update:
update address
set status = 0
where id = #{id}

mapper.select:
select  id,member_num,address,name,phone from address
where status = 1 and member_num = #{memberNum}
order by id  limit ${pageNo}, ${pageSize};

select  count(*) from address
where status = 1 and member_num = #{memberNum}
order by id  limit ${pageNo}, ${pageSize};

mapper.update:
update address
set address = #{address},
    name = #{name},
    phone = #{phone}
where id = #{id}
