mapper.update:
update member
set status = 0
where id = #{id}

mapper.select:
select  id,member_num,member_name,member_grade,member_sex,member_status,member_tel,remarks from member

    status = 1

        and member_name = #{memberName}


        and member_num = #{memberNum}


        and member_status = #{memberStatus}


order by id  limit ${pageNo}, ${pageSize};

select count(*) from member

    status = 1

        and member_name = #{memberName}


        and member_num = #{memberNum}


        and member_status = #{memberStatus}
