mapper.select:
select * from user

    status = 1

        and manager_name = #{managerName}


order by id  limit ${pageNo}, ${pageSize};

select count(*) from user

    status = 1

        and manager_name = #{managerName}

mapper.update:
update user
set status = 0
where id = #{id}

update user
set password = #{newPasswd}
where manager_email = #{email}
